}

function getComCode(){
	if (comps == "E1"){
		ComboBoxPlant.Value="PE";
	}else{
		ComboBoxPlant.Value="PS";
	}
}

function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040PF.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);//사업부
		sArgument += " MAJCD=" + quote("FPY240PF"+cmb_PLNGB.Value);//MAJCD
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
}

/********************************************************************** 
 * 내용설명 : 생성
 * 주요변수 : 
 **********************************************************************/ 
function getFPYC240Q(){
//============================================
	if(length(trim(ComboBoxPlant.Value)) < 1){
		//alert("[알림] 사업부를 선택하세요.");
		alert(setPopMessage("2041"));  //[알림] 사업부를 선택하세요.
		setMessage("2041");	
		return;
	}
	if(length(trim(cal_PLNYM.Value)) < 8){
		//alert("[알림] 기준년도을 입력해 주세요.);
		alert(setPopMessage("6048"));  //[알림] 기준년도을 입력해 주세요.
		setMessage("6048");	
		cal_PLNYM.SetFocus();
		return;
	}

	if(length(trim(cmb_PLNGB.Value)) < 1){
		//alert("[확인] 계획구분을 선택하세요.");
		alert(setPopMessage("6042"));  //[알림]계획구분을 선택하세요.
		setMessage("6042");	
		cmb_PLNGB.SetFocus();
		return;
	}
	getMst040();
	if(ds_MST040PF.GetColumn(0,"MJINT") >= substr(cal_PLNYM.Value,0,4)){
		if(confirm(substr(cal_PLNYM.Value,0,4)+"년은 이미" +"생산계획 공수산출이 완료되었습니다. 조회하시겠습니까? ") == false) return;
	}else{
		if(confirm(substr(cal_PLNYM.Value,0,4)+"년 " +"생산계획 공수산출 작업후 조회합니다. 조회하시겠습니까? ") == false) return;
		if(!setFPYC240Q()) return;
	}
	var str;
	if(cmb_GB.Value == "D"){
		MainGrid1.Visible = true;
		MainGrid2.Visible = false;
		var sKind		= "getFPYC240Q_D";						
		var sMethodName = "service::psfaclib/selectFPYC240Q_D.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var	sOutDataSet = "ds_FPYC240Q_D=ds_FPYC240Q_D";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += "@LIB1=" + quote(Lib1);
			sArgument += "@LIB2=" + quote(Lib2);
			sArgument += "@F_DAT=" + quote(substr(cal_PLNYM.Value,0,4) + "0400");
			sArgument += "@T_DAT=" + quote(toNumber(substr(cal_PLNYM.Value,0,4))+1 + "0300");
			sArgument += "@PLGB=" + quote(cmb_PLNGB.Value);
			str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	}else{
		MainGrid1.Visible = false;
		MainGrid2.Visible = true;
		var sKind		= "getFPYC240Q_S";						
		var sMethodName = "service::psfaclib/selectFPYC240Q_S.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
		var	sOutDataSet = "ds_FPYC240Q_S=ds_FPYC240Q_S";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += "@LIB1=" + quote(Lib1);
			sArgument += "@LIB2=" + quote(Lib2);
			sArgument += "@F_DAT=" + quote(substr(cal_PLNYM.Value,0,4) + "0400");
			sArgument += "@T_DAT=" + quote(toNumber(substr(cal_PLNYM.Value,0,4))+1 + "0300");
			sArgument += "@PLGB=" + quote(cmb_PLNGB.Value);
			str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	}



	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
function setFPYC240Q(){
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return false;
	}	
	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return false;
	}


	//리모트콜 실행(FPYC210rc용)
	param = cmb_PLNGB.Value + substr(cal_PLNYM.Value,0,4)+"04" + (parseInt(substr(cal_PLNYM.Value,0,4))+1)+"03"+substr(cal_PLNYM.Value,0,4)+ComboBoxPlant.Value;
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			st_Msg.Visible = false;
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//마감history에 저장
				var sKind		= "setmst049";						
				var sMethodName = "service::pscomlib/saveMST049.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " PGSRC=" + quote("FPYC240Q");
				sArgument += " PGMNM=" + quote("년생산계획 총공수산출 작업");
				sArgument += " PDATE=" + quote(substr(cal_PLNYM.Value,0,4));
				sArgument += " JOBID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
				sArgument += " BIGO1=" + quote("FPY240PF"+cmb_PLNGB.Value);
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

				return true;

			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				//alert(setPopMessage("1008"));	// 현재 시스템에 락이 걸렸습니다. 전산관리자에게 문의하세요.
				//setMessage("1008");
				return false;
			} else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "666") {
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("4020")); //[확인] 마감년월을 확인바랍니다.
				setMessage("4020");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
			return false;
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
		return false;
	}
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- svcId	Transaction 고유 ID
 * 			- rtnCd 	에러코드
 * 			- rtnMsg 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(rtnCd<0){
		ReleaseCapture();
		SetWaitCursor(false);
		alert(setPopMessage("5008"));  //[오류] 마감처리는 진행되었으나 마감년월을 관리마스타(MST040PF)에 저장 실패. 
		setMessage("5008");
	}else{
		ReleaseCapture();
		SetWaitCursor(false);
		st_Msg.Visible = false;
		alert(setPopMessage("1001")); //[알림] 정상적으로 처리가 완료되었습니다.
		setMessage("1001");
	}
}

