/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var aa = iif(cmb_PLNGB.Value == "Y1","기본계획","수정계획");
	var fName;
	if(cmb_GB.Value == "D"){
		fName   = substr(cal_PLNYM.Value,0,4)+"년"+aa+"총공수현황_상세";
	}else{
		fName   = substr(cal_PLNYM.Value,0,4)+"년"+aa+"총공수현황_집계";
	}
	var arrSheets  =  Array();  
	if(cmb_GB.Value == "D"){
		arrSheets[0]  = "MainGrid1|총공수현황_상세";
	}else{
		arrSheets[0]  = "MainGrid2|총공수현황_집계";
	}
	createExcelSheetS2(FileDialog0, fName ,arrSheets);	
}
