function getComCode(){

	http.Sync=true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	Div0.ComboBoxPlant.Value=comps;

}

function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040PF.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(Div0.ComboBoxPlant.Value);//사업부
		sArgument += " MAJCD=" + quote("FPY200"+substr(Div0.cal_PLNYM.Value,0,4)+Div0.cmb_PLNGB.Value);//MAJCD
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
}

