	}
/*
	if(ds_MST040PF.GetColumn(0,"MJINT") >= substr(Div0.cal_PLNYM.Value,0,4)){
		//alert("[알림] 이미 마감되었습니다.");
		alert(setPopMessage("5014")); //[알림] 이미 마감되었습니다.
		setMessage("5014");	
		return;
	}	
*/	
	/* 삭제 : 2019.11.27 NEWDIONE로 SSO관리전환
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
