/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : 화면조회
 * 주요변수 :  
 **********************************************************************/ 
function getFSLC203(){

	switch(ComboSQL.Index)
	{
		case 0 :

			MainGrid.BindDataset = "DS_FSLC203_1";
			MainGrid.CreateFormat();		

			var sKind		= "getFSLC203_1";						
			var sMethodName = "service::psfaclib/selectFSLC203_1.do";   // 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "DS_FSLC203_1=DS_FSLC203_1";    			// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";

			sArgument += "@TABLE='f#d30pr'"; //사업부			
			var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
			break;

		default:
			alert("선택하세요!");
	}
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{	
	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}

	// SIL47
	if (ComboSQL.Value = 1)
	{		
		var fName   = "SIL47_" + substr(getDate(), 2, 10);			
		Grid_FSLC203_1.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL47");	
	}
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBIDZ501"){
		// 조회건수가 없을경우
		if(ds_BIDZ501.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function on_cbSearchCompanyChange(obj,strCode,strText,nOldIndex,nNewIndex)	
{
	if( cb_searchCompany.index != 0 ){
		ds_VComCode_PLNT.Filter("CODE1=='" + cb_searchCompany.Value + "'");
		cb_searchCodeName.index = 0;
	}else{
		ds_VComCode_PLNT.UnFilter();
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}


