//추가 2014.04.01
function getSC000PR()
{
	var sKind		= "getSC000PF";						
	var sMethodName = "service::pscomlib/selectSC000PR.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SC000PR=ds_SC000PR";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " ADSDT=" + quote("ADS");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}
