/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{		
	//alert(ComboSQL.Value);	

	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}

	if (ComboSQL.Value = 1)
	{		
		var fName   = "SIL20_" + substr(getDate(), 2, 10);	
		//alert(FileDialog0.FilePath);
		Grid_FSLE200_1.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL20");	
	}	
	/*
	if (ComboSQL.Value = 2)
	{
		Grid_FSLE200_2.BindDataset = "DS_FSLE200_2";
		Grid_FSLE200_2.CreateFormat();		
		var fName   = "SIL031_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_2.SaveExcelEx(FileDialog0.FilePath + fName + ".xls", "SIL031", "", false, "A1", false, false, false);		
	}

	if (ComboSQL.Value = 3)
	{
		Grid_FSLE200_3.BindDataset = "DS_FSLE200_3";
		Grid_FSLE200_3.CreateFormat();		
		var fName   = "SIL04_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_3.SaveExcelEx(FileDialog0.FilePath + fName + ".xls", "SIL04", "", false, "A1", false, false, false);		
	}	
	*/
	if (ComboSQL.Value = 2)
	{		
		var fName   = "SIL05_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_4.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL05");		
	}	

	if (ComboSQL.Value = 3)
	{		
		var fName   = "SIL06_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_5.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL06");		
	}	

	if (ComboSQL.Value = 4)
	{	
		var fName   = "SIL07_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_6.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL07");		
	}

	if (ComboSQL.Value = 5)
	{		
		var fName   = "SIL14_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_7.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL14");		
	}	

	if (ComboSQL.Value = 6)
	{		
		var fName   = "SIL15_" + substr(getDate(), 2, 10);	
		Grid_FSLE200_8.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL15");		
	}	
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBIDZ501"){
		// 조회건수가 없을경우
		if(ds_BIDZ501.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function on_cbSearchCompanyChange(obj,strCode,strText,nOldIndex,nNewIndex)	
{
	if( cb_searchCompany.index != 0 ){
		ds_VComCode_PLNT.Filter("CODE1=='" + cb_searchCompany.Value + "'");
		cb_searchCodeName.index = 0;
	}else{
		ds_VComCode_PLNT.UnFilter();
	}
}

