	ds_BIDZ501.DeleteAll();
}

function ComboSQL_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	TextArea_desc.text = "";
	switch(ComboSQL.Index)
	{
		case 0 :			
			TextArea_desc.text = "실사마감 전 까지 진행되어야 함 -> 원가파트에 제공";
			break;
		case 1 :		
			TextArea_desc.text = "HD150PR 업체별 품번별 재고 HM000PR 품번별 재고";
			break;
		case 2 :
			TextArea_desc.text = "인벤토리 존재하나 ITEM MA 미존재 시 IA, 43 전표 미생성";
			break;
		case 3 :		
			TextArea_desc.text = "유/무상 사급출고 ITEM만 해당 (ITTYP='1',ITCLS='*3')되며 내재부품 재고관리 않하므로 SMD, 성형반 외 재고가 존재하면 안 됨";
			break;
		case 4 :		
			TextArea_desc.text = "당월 거래기록이 ITEM MA에 존재하지 않음";
			break;
		case 5 :		
			TextArea_desc.text = "실사 재고계산시 누락되므로 재고실사일 이후 트랙잭션이 존재하는가를 체크 (존재할 경우 해당TR ZERO화 후 작업) QTY CD가 '2'일때 - QTY CD가 '1'일때 +";
			break;
		case 6 :			
			TextArea_desc.text = "";
			break;
		case 7 :
			TextArea_desc.text = "존재할 경우 출하일자 수정 후 작업 진행 (존재하면 않 됨)";
			break;
		default:
			alert("선택하세요!");
	}																						
}

