/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	

	// 3월 또는 9월말일로 설정
	// 3~4월 일때는 3월 말
	var this_month, next_month, last_day;
	this_month = substr(today(), 0, 6);
	//alert(substr(this_month, 4, 2));

	if (substr(this_month, 4, 2) = "03" or substr(this_month, 4, 2) = "04")
	{
		next_month = substr(addmonth(today(), 1), 0, 4) + "04";
	}
	else
	{
		next_month = substr(addmonth(today(), 1), 0, 4) + "10";
	}	

	Calendar0.Value = adddate(next_month + "01", -1);

}


