/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{	
	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}

	if (ComboSQL.Value = 1)
	{		
		var fName   = "SIL22_" + substr(getDate(), 2, 10);			
		Grid_FSLE203_1.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL22");	
	}
	if (ComboSQL.Value = 2)
	{		
		var fName   = "SIL23_" + substr(getDate(), 2, 10);			
		Grid_FSLE203_2.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL23");	
	}
	if (ComboSQL.Value = 3)
	{		
		var fName   = "SIL21_" + substr(getDate(), 2, 10);			
		Grid_FSLE203_3.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL21");	
	}
	if (ComboSQL.Value = 4)
	{		
		var fName   = "SIL47_" + substr(getDate(), 2, 10);			
		Grid_FSLE203_4.SaveExcelEx(FileDialog0.FilePath + "\\" + fName + ".xls", "SIL47");	
	}			
}


/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-03-30
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBIDZ501"){
		// 조회건수가 없을경우
		if(ds_BIDZ501.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function on_cbSearchCompanyChange(obj,strCode,strText,nOldIndex,nNewIndex)	
{
	if( cb_searchCompany.index != 0 ){
		ds_VComCode_PLNT.Filter("CODE1=='" + cb_searchCompany.Value + "'");
		cb_searchCodeName.index = 0;
	}else{
		ds_VComCode_PLNT.UnFilter();
	}
}


