function ComboSQL_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	TextArea_desc.text = "";
	switch(ComboSQL.Index)
	{
		case 0 :			
			TextArea_desc.text = "";
			break;
		case 1 :		
			TextArea_desc.text = "재고실사에는 실사 스타트가 되어 상관없지만 제일가동일 마감이전에 전월시점등으로 입력된 TR를 찾아내기 위함";
			break;
		case 2 :
			TextArea_desc.text = "";
			break;
		case 3 :		
			TextArea_desc.text = "실사 시 BOM에 추가된 품번이 실사종료 후 야간배치 시에 사내BOM과 사급BOM이 동시에 구성되어 있어 무한LOOP발생";
			break;
		default:
			alert("선택하세요!");
	}																						
}
