function fn_Search()
{
	getFSME500();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_FSME500")== false) {
		return;
	}
	if(ds_FSME500.rowcount < 1) {
		fn_Search();
	}
	ds_FSME500.AddRow();
	ds_FSME500.SetColumn(ds_FSME500.currow,"COMPS",ComboBoxPlant.Value);
	ds_FSME500.SetColumn(ds_FSME500.currow,"PRODY",cmb_PRODY.Value);
	ds_FSME500.SetColumn(ds_FSME500.currow,"SEQNO",0); //등록관련내용없음
	ds_FSME500.SetColumn(ds_FSME500.currow,"TRQTY",0);
	ds_FSME500.SetColumn(ds_FSME500.currow,"ASQTY",0);
}

function fn_Delete()
{
	if(ds_FSME500.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_FSME500.DeleteRow(ds_FSME500.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

function fn_Save()
{
	setFSME500();
}

