		fn_Search();
	}
}

function ds_FSME500_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;  //transaction호출후 저장이벤트가 발생되지 않아서...
	if (strColumnID == "PSPNO"){
		//품번Check
		if(length(trim(varNewValue)) < 1) {
			return;
		}
		http.Sync 	= true;
		var sKind		= "getPrtno";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);//품번
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BM008PR.GetRowCount() < 1){
			http.usewaitcursor = true;
			alert("[알림]ITEM MASTER A에 미등록된 품번입니다.");
			//alert(setPopMessage("6032"));//[알림]ITEM MASTER A에 미등록된 품번입니다.
			//setMessage("6032");
			return false;
		}
	}
	if (strColumnID == "PROCS"){
		if(length(trim(varNewValue)) < 1) {
			return;
		}
		var sKind		= "getBM001PR";						
		var sMethodName = "service::pscomlib/selectBM001PR.do";    // 서버에서 호출 될 Method 명
		var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_BM001PR=ds_BM001PR";  	   // 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB1=" + quote(Lib1);
			sArgument += " PROCS=" + quote(varNewValue); //공정코드
		http.Sync=true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync=false;
		if(ds_BM001PR.rowcount<1){
			//alert("[알림] PROCESS MA에 미등록된 공정코드입니다.");
			alert(setPopMessage("4746"));//[알림] PROCESS MA에 미등록된 공정코드입니다.
			setMessage("4746");			
			return false;
		}
	}
	http.usewaitcursor = true;
}

function ds_FSME500_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "TRQTY"){
		if(varNewValue == "" ){
			ds_FSME500.SetColumn(nRow,"TRQTY",0);//수량
		}
	}
	if (strColumnID == "ASQTY"){
		if(varNewValue == "" ){
			ds_FSME500.SetColumn(nRow,"ASQTY",0);//수량
		}
	}
}

function ds_FSME500_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_FSME500.GetRowType(ds_FSME500.currow)=="update" ||ds_FSME500.GetRowType(ds_FSME500.currow)=="insert" ){
		var p_PSPNO=ds_FSME500.GetColumn(ds_FSME500.currow,"PSPNO");	//품번
		var p_PROCS=ds_FSME500.GetColumn(ds_FSME500.currow,"PROCS");	//공정
		var p_TRQTY=ds_FSME500.GetColumn(ds_FSME500.currow,"TRQTY");	//생산실적
		var p_ASQTY=ds_FSME500.GetColumn(ds_FSME500.currow,"ASQTY");	//포장수량

		if(length(p_PSPNO)!=0){
			if(length(p_PROCS)==0){
				alert("[알림] 공정코드를 입력하세요.");
				//alert(setPopMessage("2026"));//[알림] 공정코드를 입력하세요.
				//setMessage("2026");
				result=false;
			}else if(p_ASQTY > p_TRQTY){
				alert("[알림]포장수량이 생산실적보다 클수 없습니다.");
				//alert(setPopMessage("6124"));//[알림]포장수량이 생산실적보다 클수 없습니다.
				//setMessage("6124");
				result=false;
			}
		}		
	return result;		
	}
}
