	getComCode(); //공장구분 선택

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	//ComboBoxPlant.Enable = false; 
}


function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");	
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getFZME200(){

	var sKind		= "getFZME200";						
	var sMethodName = "service::psfaclib/selectFZME200.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FZME200=ds_FZME200";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@CPCAR=" + quote(ed_CARCD.Text); //차종코드
		sArgument += "@CPRNG=" + quote(ed_RGNCD.Text); //지역코드
		sArgument += "@PRTNO=" + quote(ed_PRTNO.Text); //품번코드

	if(Cmb_CPGUB.Value != "전체"){
		sArgument += "@CPGUB=" + quote(Cmb_CPGUB.Value); //초품구분
	}

	if(Cmb_CPAGB.Value != "전체"){
		sArgument += "@CPAGB=" + quote(Cmb_CPAGB.Value); //초품생산구분
	}	

	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));		
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setFZME200(){

	var sKind		= "setFZME200";						
	var sMethodName = "service::psfaclib/saveFZME200.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_FZME200=ds_FZME200:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FZME200_CHECK=ds_FZME200_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);

		//sArgument += " CARCD=" + quote(ed_CARCD.Text); //차종코드
		//sArgument += " RGNCD=" + quote(ed_RGNCD.Text); //지역코드		

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;


}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_FZME200_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_FZME200_CHECK.GetRowCount();i++){
			//alert("CODE : " +ds_MSTZ100Q_CHECK.GetColumn(i,"MSTCD") + " = " + ds_MSTZ100Q_CHECK.GetColumn(i,"RTMSG"));
		}		
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
	getFZME200();  //error항목이 존재시에는 재조회
}

