function ds_FZME200_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "PRTNO" ){
		//품번Check,조회
		http.Sync = true;
		var sKind		= "getBM008PR";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);

		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

		if (ds_BM008PR.GetRowCount() < 1){
			alert("Item Master에 미등록된 품번입니다.");  // [경고] Item Master에 미등록된 품번입니다.
			//setMessage("1509");	
			return false;
		}	
		can_ITDSC = ds_BM008PR.GetColumn(nRow,"ITDSC");		
	} else if ( strColumnID == "CPDAT" ){
		can_CPDAT = ds_FZME200.GetColumn(nRow,"CPDAT");
	}
}

function ds_FZME200_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_FZME200.GetRowType(ds_FZME200.currow)=="update" ||ds_FZME200.GetRowType(ds_FZME200.currow)=="insert" ){

		var cpgub=ds_FZME200.GetColumn(ds_FZME200.currow,"CPGUB");
		var cpagb=ds_FZME200.GetColumn(ds_FZME200.currow,"CPAGB");

		if(length(cpgub)!=0){
			if(length(cpgub)==0){
				alert(setPopMessage("4686"));
				setMessage("4686");
				//MainGrid.AutoEnter=true;
				result=false;
			}
			else if(length(cpgub)==0){
				alert(setPopMessage("4687"));
				setMessage("4687");
				//MainGrid.AutoEnter=true;
				result=false;
			}
		}
	return result;		
	}
}

function ds_FZME200_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{		
	if (strColumnID == "PRTNO"){
		ds_FZME200.SetColumn(nRow,"ITDSC", can_ITDSC);
		ds_FZME200.SetColumn(nRow,"CPGUB","1");
		ds_FZME200.SetColumn(nRow,"CPAGB","N");
	}
	else if (strColumnID == "CPDAT"){
		if (ds_FZME200.GetColumn(nRow, "CPAGB") == "Y"){
			alert("양산된 제품은 날짜 변경이 적용되지 않습니다.");
		}
	}
}
