function On_LoadCompleted(obj)
{
	getTitle();	
	getDesign();
	getComCode();

	Calendar0.Value = today();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	Cmb_com.SetFocus();

}

/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");	
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}


/********************************************************************** 
 * 개 발 자 : 민명규 
 * 개발일자 : 2014-01-04
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getFZME200Q(){

	http.Sync = true;

	var sKind		= "getFZME200Q";						
	var sMethodName = "service::psfaclib/selectFZME200Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_FZME200Q=ds_FZME200Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@LIB3=" + quote(Lib3);
	sArgument += "@COMPS=" + quote(CmbCOM.Value);
	sArgument += "@PRTNO=" + quote(ed_PRTNO.Value);
	sArgument += "@CARCD=" + quote(ed_CARCD.Text); //차종코드
	sArgument += "@RGNCD=" + quote(ed_RGNCD.Text); //지역코드
	if (Cmb_CPAGB.Value != "전체") {
		sArgument += "@CPAGB=" + quote(Cmb_CPAGB.Value);
	}

	if (Cmb_CPGUB.Value != "전체") {
		sArgument += "@CPGUB=" + quote(Cmb_CPGUB.Value);
	}

	if (Cld_DUEDT1.Value != "" && Cld_DUEDT2.Value != "") {
		sArgument += "@DUEDT1=" + quote(Cld_DUEDT1.Value); 
		sArgument += "@DUEDT2=" + quote(Cld_DUEDT2.Value); 

	} else if(Cld_DUEDT1.Value != "" && Cld_DUEDT2.Value == ""){
		alert("종료 생산예정일을 입력하여 주십시오.");
		return false;
	} else if(Cld_DUEDT1.Value == "" && Cld_DUEDT2.Value != ""){
		alert("시작 생산예정일을 입력하여 주십시오.");
		return false;
	}

	if (Cld_CPADT1.Value != "" && Cld_CPADT2.Value != "") {
		sArgument += "@CPADT1=" + quote(Cld_CPADT1.Value); 
		sArgument += "@CPADT2=" + quote(Cld_CPADT2.Value); 

	} else if(Cld_CPADT1.Value != "" && Cld_CPADT2.Value == ""){
		alert("종료 생산일을 입력하여 주십시오.");
		return false;
	} else if(Cld_CPADT1.Value == "" && Cld_CPADT2.Value != ""){
		alert("시작 생산일을 입력하여 주십시오.");
		return false;
	}


	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}


	http.Sync=false;
}


