 **********************************************************************/ 
function setExcel()
{
	var fName   = "초품지시서";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|초품지시서";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

