function setExcel()
{
	var fName   = "퇴직금정산이력";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|퇴직금정산이력";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

