//권한조회
function getHASS04(){

	var result = true;

	var sKind		= "getHASS04";						
	var sMethodName = "service::pshumlib/selectHASS04.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";
	var sOutDataSet = "ds_HUM090PF=ds_HUM090PF";
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(CmbCOM.Value);		
		sArgument += " DIOID=" + quote(usrID);		
		//sArgument += " DIOID=" + quote("H1145232");		
		//sArgument += " DIOID=" + quote("H9140059");		
		sArgument += " USER_YN=" + quote("1");	//사용이 1인것만조회
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;

	if(ds_HUM090PF.rowcount<1){	
		result = false;
	}
	return result;	
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getHASS03(){

	http.Sync = true;

	var DPPAT1=ds_HUM090PF.GetColumn(0,"DPPAT1");

	var sKind		= "getHASS03";						
	var sMethodName = "service::pshumlib/selectHASS01.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HASS01=ds_HASS01";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@LIB3=" + quote(Lib3);
	sArgument += "@COMPS=" + quote(CmbCOM.Value);
	sArgument += "@DPPAT1=" + quote(DPPAT1);//팀장부서코드

	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	

	if(ds_HASS01.rowcount <1){
		return;
	}
	ds_HASS01.InsertRow(0);
	ds_HASS01.SetColumn(0,"DPCODE","99");
	ds_HASS01.SetColumn(0,"DPCONM","전체");	

	TreeView0_OnClick(TreeView0,0,0);
}
