}

/********************************************************************** 
 * 내용설명 : 부서 그리드 클릭 이벤트
 **********************************************************************/ 
function TreeView0_OnClick(obj,nOldRow,nNewRow)
{ 

	ds_HASS01_dept.ClearData();

	var DPCODE = ds_HASS01.GetColumn(nNewRow, "DPCODE");

	http.Sync = true;
	var sKind		= "selectHASS01_dept";
	var sMethodName = "service::pshumlib/selectHASS01_dept.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HASS01_dept=ds_HASS01_dept";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
	if(DPCODE <> '99'){
		sArgument += " DPTCD=" + quote(DPCODE);
	}else{
		var arrDPCODE= "('";
		for(i=1; i<ds_HASS01.rowcount; i++){
			if(i == ds_HASS01.rowcount-1){
				arrDPCODE = arrDPCODE+ds_HASS01.GetColumn(i,"DPCODE") + "')";
			}else{
				arrDPCODE = arrDPCODE+ds_HASS01.GetColumn(i,"DPCODE") + "','";
			}
		}
		sArgument += " arrDPCODE=" + quote(arrDPCODE);
	}	
	sArgument += " CNDCD=" + quote("");//재직자만보여줌,

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	STA1.Text = trim(ds_HASS01.GetColumn(nNewRow, "DPCONM"));
	STA2.Text = ds_HASS01_dept.RowCount();	
}

