/********************************************************************** 
 * 내용설명 : 
 * 주요변수 : 
 **********************************************************************/ 
 function setHAY011UC(){
	if (ds_HAY011PF.GetRowCount() < 1) {
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}

	if(confirm("소득공제신고서를 등록하시겠습니까?") == false) return;

	//파일 업로드(저장버튼을 누른경우 엑셀파일이 저장됨.
	var path =  Div1.ed_path.Text;
	ProFile.FileName = path;
	//var imgName = substr(ComboBoxPlant.Value,0,1) + ds_HAY011PF.GetColumn(0,"SABUN") + ".xls";
	var imgName ="";
	if(ComboBoxPlant.Value=="E1"){
		//imgName = substr(ComboBoxPlant.Value,0,1) + lpad(trim(ds_HAY011PF.GetColumn(0,"SABUN")),"0",6) + ".xls";	//20170210
		imgName = substr(ComboBoxPlant.Value,0,1) + lpad(trim(ds_HAY011PF.GetColumn(0,"SABUN")),"0",6) + substr(file_name, indexOf(file_name,"."));
	}else{
		//imgName = substr(ComboBoxPlant.Value,0,1) + lpad(trim(ds_HAY011PF.GetColumn(0,"SABUN")),"0",7) + ".xls";	//20170210
		imgName = substr(ComboBoxPlant.Value,0,1) + lpad(trim(ds_HAY011PF.GetColumn(0,"SABUN")),"0",7) + substr(file_name, indexOf(file_name,"."));
	}
	//데이터셋에 로우 추가
	ds_uploadFile.addRow();
	//다이얼로그에서 선택한 파일의 경로명(파일명포함)을 데이터셋에 저장
	ds_uploadFile.SetColumn(ds_uploadFile.row,"fullname", path);
	//다이얼로그에서 선택한 파일명을 데이터셋에 저장
	ds_uploadFile.SetColumn(ds_uploadFile.row,"filename", imgName);	

	if(!file_Upload()){
		//ds_uploadFile.DeleteAll();
		ds_uploadFile.ClearData();
		FileDialog0.FileName= "";
		alert("[알림]파일업로드중 오류가 발생하였습니다. 다시 시도해주시기 바랍니다.");
		return false;
	}
	else {
		FileDialog0.FileName= "";
	}

	var p_PTJYM="";
	p_PTJYM=substr(cmb_PTJYM.Value,0,4)+"13";

 	http.Sync = true;
	var sKind		= "setHAY011UC";						
	var sMethodName = "service::pshumlib/saveHAY011UC.do";
	var sInDataSet  = "ds_HAY011PF=ds_HAY011PF ds_HAY021PF=ds_HAY021PF ds_HAY041PF=ds_HAY041PF ds_HAY061PF=ds_HAY061PF ds_HAY063PF=ds_HAY063PF";
	var sOutDataSet = "";
	var sArgument   = "";
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " PTJYM=" + quote(p_PTJYM);
		sArgument += " SABUN=" + quote(sabuncheck);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;

 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}	
