//연말정산 해당년 자동계산
function setPTJYM(){
	cmb_PTJYM.Value=today();//Default 값
	if(parseInt(substr(today(),4,2)) < 4){
		cmb_PTJYM.Value=parseInt(substr(today(),0,4))-1+"1231";
	}
}

/********************************************************************** 
 * 내용설명 : Excel FILE 가져오기
 * 주요변수 : 
 **********************************************************************/ 

function Div1_btn_FORMDOWN_OnClick(obj)
{
	//연말정산파일저장경로
	var gImgPath =substr(cmb_PTJYM.Value,0,4)+"Year/";//년도별 폴더경로에 다운받을저장

	//다운로드 받을 파일명을 취득하여 변수에 설정(클릭한 로우의 파일)
	var str_param ="";

	var str_exeNm = ".xlsx";
	if(ComboBoxPlant.Value =="E1"){
		 str_param = substr(cmb_PTJYM.Value,0,4)+"YearTax_KE"+str_exeNm;
	}else if(ComboBoxPlant.Value =="C1"){
		 str_param = substr(cmb_PTJYM.Value,0,4)+"YearTax_KAC"+str_exeNm;
	}else if(ComboBoxPlant.Value =="H1"){
		 str_param = substr(cmb_PTJYM.Value,0,4)+"YearTax_KAH"+str_exeNm;
	}else if(ComboBoxPlant.Value =="S1"){
		 str_param = substr(cmb_PTJYM.Value,0,4)+"YearTax_KAS"+str_exeNm;
	}

	//다운로드 받을 파일이 저장되어있는 서버 경로와 파일명
	var str_param_server = imgUPDOWNpath + gImgPath+str_param;

	//-----------  다이얼로그창  설정------------------
    //다이얼로그창에 다운받을 파일명 설정
	FileDialog0.FileName= str_param;
    //다이얼로그창에 다운받을 기본경로 설정
	FileDialog0.FilePath= "C:\\";
	//다이얼로그창 버튼 SAVE로 설정
	FileDialog0.Type = "SAVE";
	//확장자가 없을 때 기본 확장자 설정
	FileDialog0.AppendExtDefault = "TRUE";

	//다이얼로그 생성(확인 버튼 클릭시 true, 취소버튼을 클릭시 false 반환)
	//확인 버튼 클릭시만 처리 계속
	if(FileDialog0.Open()){

		//다운로드 받을 경로(로컬)
		//var str_param_full = "C:/download/" + Dataset1.getColumn(nRow,"filename"); //다이얼로그창 미사용시(고정폴더)
		var str_param_full = FileDialog0.FilePath + "\\" + FileDialog0.FileName;


		trace("파일명:" + str_param);
		trace("경로(파일명):" + str_param_full);

		//스크립트 파일의 함수 호출 
		var file_size = CyHttpFile0.GetFileSizeLocal(str_param_server);
		trace("파일사이즈:" + file_size);
		var ret = gfn_FileRead(CyHttpFile0, str_param_full,str_param_server, file_size, 1, "prog_bar", "");

		//호출함수 리턴값 처리
		if ( ret[0] == "FAIL" )
		{
			alert(ret[1]);
		}else{
			alert("[알림]양식이 정상적으로 PC에 저장되었습니다.");
		}
	}
}

