//연금저축TABLE 체크
function chkExcelData2(){
	//소득공제구분, 금융기관코드, 계좌번호 없는 ROW삭제
	for(i=ds_HAY041PF.rowcount; i>=0; i--){
		if(length(trim(ds_HAY041PF.GetColumn(i,"INCCD")))==0  || length(trim(ds_HAY041PF.GetColumn(i,"BNKCD")))==0 || length(trim(ds_HAY041PF.GetColumn(i,"GJBNB")))==0){
		//if(length(trim(ds_HAY041PF.GetColumn(i,"INCCD")))==0 && length(trim(ds_HAY041PF.GetColumn(i,"BNKCD")))==0){
			ds_HAY041PF.DeleteRow(i);
		}
	}

	for(var z=0;z<ds_HAY041PF.rowcount; z++){
		if(length(trim(ds_HAY041PF.GetColumn(z,"PAYTT"))) < 1) ds_HAY041PF.SetColumn(z,"PAYTT",0);//불입금액
		if(length(trim(ds_HAY041PF.GetColumn(z,"PAYTM"))) < 1) ds_HAY041PF.SetColumn(z,"PAYTM",0);//납입금액
	}
}

//2015년도 연말정산 추가내용
//의료비명세서TABLE 체크
function chkExcelData3(){
	//지급금액이 없는 ROW삭제
	for(i=ds_HAY061PF.rowcount; i>=0; i--){
		if(length(trim(ds_HAY061PF.GetColumn(i,"HOPMON")))==0){
			ds_HAY061PF.DeleteRow(i);
		}
	}
	for(var z=0;z<ds_HAY061PF.rowcount; z++){
		if(length(trim(ds_HAY061PF.GetColumn(z,"FORCDA"))) < 1) ds_HAY061PF.SetColumn(z,"FORCDA",0);//외국인코드A
		if(length(trim(ds_HAY061PF.GetColumn(z,"FORCDB"))) < 1) ds_HAY061PF.SetColumn(z,"FORCDB",0);//외국인코드B
		if(length(trim(ds_HAY061PF.GetColumn(z,"HOPCNT"))) < 1) ds_HAY061PF.SetColumn(z,"HOPCNT",0);//건수
	}
}
//2015년도 연말정산 추가내용
//기부금명세서TABLE 체크
function chkExcelData4(){
	//지급금액이 없는 ROW삭제
	for(i=ds_HAY063PF.rowcount; i>=0; i--){
		if(length(trim(ds_HAY063PF.GetColumn(i,"KIBMON")))==0){
			ds_HAY063PF.DeleteRow(i);
		}
	}
	for(var z=0;z<ds_HAY063PF.rowcount; z++){
		if(length(trim(ds_HAY063PF.GetColumn(z,"FORCDA"))) < 1) ds_HAY063PF.SetColumn(z,"FORCDA",0);//외국인코드A
		if(length(trim(ds_HAY063PF.GetColumn(z,"FORCDB"))) < 1) ds_HAY063PF.SetColumn(z,"FORCDB",0);//외국인코드B
		if(length(trim(ds_HAY063PF.GetColumn(z,"KIBCNT"))) < 1) ds_HAY063PF.SetColumn(z,"KIBCNT",0);//건수
	}
}
