var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();
	getDesign();
	getComCode();	
	gFcOnTopButtonColor("2.7"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	ComboBoxPlant.SetFocus();
	//ed_SABUN.Text="0340044";
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = "white";
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getHAYZ071(){

	if(!validationChk()){
		return;
	}
	var p_PTJYM="";
	if(cmb_GUBUN.Value = 1){//연말정산인 경우
		p_PTJYM=substr(cmb_PTJYM.Value,0,4)+"13";
	}else{//중도정산인경우
		p_PTJYM =substr(cmb_PTJYM.Value,0,6);
	}

	var sKind		= "getHAYZ071";						
	var sMethodName = "service::pshumlib/selectHAYZ071.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HAYZ071=ds_HAYZ071";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@SABUN=" + quote(ed_SABUN.Text);			
		sArgument += "@TOTYM=" + quote(p_PTJYM);	//정산년월
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}	
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setHAYZ071(){

	var sKind		= "setHAYZ071";						
	var sMethodName = "service::pshumlib/saveHAYZ071.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_HAYZ071=ds_HAYZ071:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HAYZ071_CHECK=ds_HAYZ071_CHECK";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		//sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		//sArgument += " SABUN=" + quote(ed_SABUN.Text);			
		//sArgument += " TOTYM=" + quote(p_PTJYM);	//정산년월
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_HAYZ071_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_HAYZ071_CHECK.GetRowCount();i++){		
			alert(ds_HAYZ071_CHECK.GetColumn(i,"RTMSG"));
		}
		//getHAYZ071();
		return;
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){

	var result = true;
	ed_SABUN.BKColor="white";
	cmb_PTJYM.BKColor="white";

	if(cmb_PTJYM.Value == ""){
		cmb_PTJYM.BKColor="paleturquoise";
		//alert("[확인] 정산년월을 확인바랍니다.");	
		alert(setPopMessage("4036"));//[확인] 정산년월을 확인바랍니다.
		setMessage("4036");
		cmb_PTJYM.SetFocus();
		result = false;
	}
	else if(length(trim(st_KORNM.Text)) == 0){
		ed_SABUN.BKColor="paleturquoise";
		//alert("[알림]사번을 입력하세요.");	
		alert(setPopMessage("6120"));//[알림]사번을 입력하세요.
		setMessage("6120");
		ed_SABUN.SetFocus();
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "소득정산 집계자료수정";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|소득정산 집계자료수정";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

