var strSeparator = "	";

var Lib1 = G_ds_user.GetColumn(0,"LIB1");
var Lib2 = G_ds_user.GetColumn(0,"LIB2");
var Lib3 = G_ds_user.GetColumn(0,"LIB3");    //인사급여 LIBRARY(PSEDBLIB           PSCDBLIB.PSHDBLIB.PSSDBLIB)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	gFcOnTopButtonColor("1");
}
/********************************************************************** 
 * 내용설명 : 사업부 선택 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;
	ComboBoxPlant.Enable = false; 	
	http.Sync = false;
}
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	//MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getHAYZ092Q(){

	if(!validationChk()){
		return;
	}
	if(ComboBoxPlant.Value="C1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSCDBLIB";
	}else if(ComboBoxPlant.Value="H1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSHDBLIB";
	}else if(ComboBoxPlant.Value="S1"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
		Lib3 = "PSSDBLIB";
	}
	var sKind		= "getHAYZ092Q";						
	var sMethodName = "service::pshumlib/selectHAYZ092Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HAYZ092Q=ds_HAYZ092Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PTJYM=" + quote(substr(cmb_PTJYM.Value,0,4)); //기준년도
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){

	var result = true;
	cmb_PTJYM.BKColor="white";

	if(cmb_PTJYM.Value == ""){
		cmb_PTJYM.BKColor="paleturquoise";
		alert("[확인] 정산년월을 확인바랍니다.");	
		//alert(setPopMessage("4036"));//[확인] 정산년월을 확인바랍니다.
		setMessage("4036");
		cmb_PTJYM.SetFocus();
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "보수총액신고";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|보수총액신고";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

