function on_LoadCompleted(obj){
	getTitle();
	getDesign();
	getComCode();	
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	ComboBoxPlant.SetFocus();
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getHMDZ280(){


	var sKind		= "getHMDZ280";						
	var sMethodName = "service::pshumlib/selectHMDZ280.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HMDZ280=ds_HMDZ280";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);			
		sArgument += "@SABUN=" + quote(ed_SABUN.Text);			
		sArgument += "@CNDCD=" + quote(chK_CNDCD.Value);	//퇴사자포함여부
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setHMDZ280(){

 	if(!validationChk()){
		return;
	}
 	if(ds_HMDZ280.GetRowType(ds_HMDZ280.currow)=="update" ||ds_HMDZ280.GetRowType(ds_HMDZ280.currow)=="insert" ){
		if(!ds_HMDZ280_CanRowPosChange()){
			return;
		}
	} 

	var sKind		= "setHMDZ280";						
	var sMethodName = "service::pshumlib/saveHMDZ280.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_HMDZ280=ds_HMDZ280:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_HMDZ280_CHECK=ds_HMDZ280_CHECK";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " SABUN=" + quote(ed_SABUN.Text);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_HMDZ280_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_HMDZ280_CHECK.GetRowCount();i++){		
			alert("사고발생일 : " +ds_HMDZ280_CHECK.GetColumn(i,"ACDAT") + " = " + ds_FHDC600_CHECK.GetColumn(i,"RTMSG"));
		}
		getHMDZ280();        //error항목이 존재시에는 재조회		
		return;		
	}// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){

	var result = true;
	ed_SABUN.BKColor="white";

	if(length(trim(st_KORNM.Text)) == 0){
		ed_SABUN.BKColor="paleturquoise";
		alert("[알림]사번을 입력하세요.");	
		//alert(setPopMessage("6120"));//[알림]사번을 입력하세요.
		//setMessage("6120");
		ed_SABUN.SetFocus();
		result = false;
	}
	return result;
}

