function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_HMDZ280")== false) {
		return;
	}
	if(ds_HMDZ280.rowcount < 1) {
		fn_Search();
	}
	ds_HMDZ280.AddRow();
	ds_HMDZ280.SetColumn(ds_HMDZ280.currow,"SABUN",ed_SABUN.Text);
	ds_HMDZ280.SetColumn(ds_HMDZ280.currow,"KORNM",st_KORNM.Text);
	ds_HMDZ280.SetColumn(ds_HMDZ280.currow,"ACCYY",0);//row추가시 Default
	//ds_HMDZ280.SetColumn(ds_HMDZ280.currow,"ACCCD",0);//row추가시 Default

	//MainGrid.SetCellPos(1);
	//MainGrid.SetFocus();
}

function fn_Delete()
{
	if(ds_HMDZ280.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_HMDZ280.DeleteRow(ds_HMDZ280.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

