function imgCODE_OnClick(obj,nX,nY)
{
	var sTemp="zzzzzzzzzz";
	var selectnumber = "H6";
	var PAYCD = 3; //1이면 기능직 2이면 직반장 3이면 전체
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" +  sTemp + " " + "PAYCD=" + PAYCD,584,528);
	if (length(rtn) != 0){
		ed_SABUN.Text=rtn[0];
		st_KORNM.Text=rtn[1];
	}	
}

function ed_SABUN_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getHMD020PF";		
	var sMethodName = "service::pscomlib/selectINSAMasterPopup.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_HMD020PF=ds_Select";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " LIB3=" + quote(Lib3);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument  += " SABUN=" + quote(ed_SABUN.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_HMD020PF.GetRowCount() < 1){
		alert(setPopMessage("4062")); //[확인] 사용자가 존재하지 않습니다.
		setMessage("4062");
		st_KORNM.Text = "";
		ed_SABUN.SetFocus();
		return;
	}
	st_KORNM.Text = ds_HMD020PF.GetColumn(0,"A2");	//이름
	http.usewaitcursor = true;	
}

function ed_SABUN_OnCharChanged(obj,strPreText,strPostText)
{
	st_KORNM.Text ="";
}

function ds_HMDZ280_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "ACDAT"){//사고발생일
		if(varNewValue!=""){
			if(ds_HMDZ280.FindRow("ACDAT", trim(varNewValue)) >= 0){
				alert("[경고] 동일한 사고발생일이 등록되어 있습니다."); 
				//alert(setPopMessage("6508"));  //[경고] 동일한 사고발생일이 등록되어 있습니다.
				setMessage("6508");	
				return false;
			}
		}
	}
}


function ds_HMDZ280_CanRowPosChange(obj,nOldRow,nRow)
{
	http.usewaitcursor = false;

	var p_ACDAT=ds_HMDZ280.GetColumn(ds_HMDZ280.currow,"ACDAT"); //사고발생일
	var p_ESDAT=ds_HMDZ280.GetColumn(ds_HMDZ280.currow,"ESDAT"); //시작일
	var p_EEDAT=ds_HMDZ280.GetColumn(ds_HMDZ280.currow,"EEDAT"); //종료일

	if(length(p_ACDAT)!=0){
		if(length(p_ESDAT)==0){	
			alert("[알림]시작일을 입력하세요.");
			//alert(setPopMessage("6509")); //[알림]시작일을 입력하세요.
			setMessage("6509");	
			return false;
		}
		if(p_ACDAT > p_ESDAT){	
			alert("[알림]사고발생일이 시작일 보다 클수 없습니다.");
			//alert(setPopMessage("6512")); //[알림]사고발생일이 시작일 보다 클수 없습니다.
			setMessage("6512");	
			return false;
		}		
		if(length(p_EEDAT)==0){	
			alert("[알림]종료일을 입력하세요.");
			//alert(setPopMessage("6510")); //[알림]종료일을 입력하세요.
			setMessage("6510");	
			return false;
		}

		if(p_ESDAT > p_EEDAT){	
			alert("[알림]종료일이 시작일 보다 클수 없습니다.");
			//alert(setPopMessage("6511")); //[알림]종료일이 시작일 보다 클수 없습니다.
			setMessage("6511");	
			return false;
		}
	}
	if(length(p_ACDAT)==0){
		ds_HMDZ280.DeleteRow(ds_HMDZ280.currow);
	}	
	return true;
	http.usewaitcursor = true;
}

function ds_HMDZ280_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "ACDAT"){//교육득점
		if(length(trim(varNewValue))!=0){
			ds_HMDZ280.SetColumn(nRow,"ACCYY",substr(ds_HMDZ280.GetColumn(nRow,"ACDAT"),0,4));
		}else{
			ds_HMDZ280.SetColumn(nRow,"ACCYY",0);
		}
	}
	/*
	if(strColumnID == "ACCCD"){//발생구분
		if(length(trim(varNewValue))==0){
			ds_HMDZ280.SetColumn(nRow,"ACCCD",0);
		}
	}
	*/
}
