#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//ed_DPCODE.Text="3310";
	cmb_SDATED.Value=substr(cmb_SDATED.Value,0,6)+"01";
	getHUM020PF();
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false;
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getHUM020PF(){
	var sKind		= "getHUM020PF";						
	var sMethodName = "service::pshumlib/selectHUM020PF.do";
	var sInDataSet  = "";
	var sOutDataSet = "ds_HUM020PF=ds_HUM020PF";
	var sArgument   = "";
		sArgument   += " LIB3=" + quote(Lib3);
		sArgument   += " PACOD=" + quote("KJUNUP");		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	st_KJUN1BM.Value=ds_HUM020PF.GetColumn(0,"PADA1");
	st_KJUN1BA.Value=parseFloat(ds_HUM020PF.GetColumn(0,"PADA1"))+1;
	st_KJUN2BB.Value=ds_HUM020PF.GetColumn(0,"PADA2");
	st_KJUN2BI.Value=parseFloat(ds_HUM020PF.GetColumn(0,"PADA2"))+1;
}
function getHWD075Q(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getHWD075Q";						
	var sMethodName = "service::pshumlib/selectHWD075Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_HWD075Q=ds_HWD075Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@DPCODE=" + quote(ed_DPCODE.Text); //부서코드
		sArgument += "@PACOD=" + quote("KJUNUP"); //
	var str = dialog("Common::PGMLOAD_HUM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_DPCODE.BKColor="white";

	if (length(trim(ed_DPCODE.Value)) == 0){
		ed_DPCODE.BKColor="paleturquoise";
		alert("[알림]필수입력항목입니다.");
		//alert(setPopMessage("2544"));//[경고] 필수입력항목입니다. (부서코드)
		//setMessage("2544");
		result = false;
	}else{
		var sKind		= "getMST650PF";		
		var sMethodName = "service::pscomlib/selectMST650.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST650PF=ds_MST650PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " LIB3=" + quote(Lib3);
			sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument  += " DPCODE=" + quote(ed_DPCODE.Text);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST650PF.GetRowCount() < 1){
			//alert("[알림]부서코드확인바랍니다."); //[알림]부서코드확인바랍니다.
			alert(setPopMessage("4708")); //[알림]부서코드확인바랍니다.
			setMessage("4708");	
			st_DPCONM.Text="";
			result = false;
		}
		else{
			st_DPCONM.Text=trim(ds_MST650PF.GetColumn(0,"DPCONM"));
		}
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
