function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");    //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");             //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                 //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //30
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32	
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getMST050";						
	var sMethodName = "service::pscomlib/selectMST050.do"; 	
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST050PF=ds_MST050PF";
	var sArgument   = "";
		sArgument   += " LIB2=" + quote(Lib2);
		sArgument   += " COMPS=" + quote(comps);
		sArgument   += " GRPCD='PRD'";
		sArgument   += " MSTGU='$'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	ds_MST050PF.InsertRow(0);
	ds_MST050PF.SetColumn(0,"mstcd","");
	ds_MST050PF.SetColumn(0,"mstrm","[전체]");
	cb_itcls1.index = 0;	
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBHZC301Q(){
	var sKind		= "getBHZC301Q";						
	var sMethodName = "service::psimmlib/selectBHZC301Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BHZC301Q=ds_BHZC301Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@HOUSE=" + quote(cb_house.Value);
	sArgument += "@COMPANY=" + quote(company);
	sArgument += "@PLANN=" + quote(ed_PLANN.Text);
	sArgument += "@PRDCD=" + quote(cb_itcls1.Value);
	sArgument += "@GUBUN=" + quote(cb_Gubun.Value);
	sArgument += "@PFNM='F#D30PR'";

	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-06
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "자재현재고현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|현재고현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
		alert("4 CALLBACK");
	}else if(svcId=="getBHZC301Q"){
		// 조회건수가 없을경우
		if(ds_BHZC301Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}

