function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	//getMst040();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)

	ed_PLANN1.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){
	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBZZE401Q(){
	if(length(trim(ed_SEQ.Value)) < 1){
		alert(setPopMessage("2033"));  //[알림] 구분 코드를 입력해 주세요.
		setMessage("2033");	
		ed_SEQ.SetFocus();
		return;
	}
	var p_PLA1,p_PLA2;
	p_PLA1 = iif (length(trim(ed_PLANN1.Text)) == 0, '0', trim(ed_PLANN1.Text));
	p_PLA2 = iif (length(trim(ed_PLANN2.Text)) == 0, '99999', trim(ed_PLANN2.Text));


	//사내창고
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAC'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
		sArgument   += " LIB1=" + quote(Lib1);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseHACT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouseHAC = "('";
	for (var i=0 ; toNumber(length(arrHouseHACT)) > i ; i++){
		if (i == length(arrHouseHACT)-1){
			arrHouseHAC = arrHouseHAC+substr(arrHouseHACT,i,1) + "')";
		}else{
			arrHouseHAC = arrHouseHAC+substr(arrHouseHACT,i,1) + "','";
		}
	}
	//사급창고
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAP'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
		sArgument   += " LIB1=" + quote(Lib1);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseHAPT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouseHAP = "('";
	for (var i=0 ; toNumber(length(arrHouseHAPT)) > i ; i++){
		if (i == length(arrHouseHAPT)-1){
			arrHouseHAP = arrHouseHAP+substr(arrHouseHAPT,i,1) + "')";
		}else{
			arrHouseHAP = arrHouseHAP+substr(arrHouseHAPT,i,1) + "','";
		}
	}

	var sKind		= "getBHZE209Q";						
	var sMethodName = "service::psimmlib/selectBHZE209Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BHZE209Q=ds_BHZE209Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@SEQNUM=" + quote(ed_SEQ.Value);
	sArgument += "@PLANN1=" + quote(p_PLA1);
	sArgument += "@PLANN2=" + quote(p_PLA1);
	sArgument += "@HOUSE_HAC=" + quote(arrHouseHAC);  //사내창고
	sArgument += "@HOUSE_HAP=" + quote(arrHouseHAP);  //사급창고
	sArgument += "@CURYM=" + quote(substr(nowDate,0,6)); //현재년월
	sArgument += "@D30PR=" + quote("F#D30PR");
	sArgument += "@GUB=" + quote(cmb_CLSGU.Value);
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "부품재고보유현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|부품재고보유현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBZZE401Q"){
		// 조회건수가 없을경우
		if(ds_BZZE401Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
