function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	gFcOnTopButtonColor("2.1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 

	MainGrid.UserData = MainGrid.Height;
	getMST040PF();
	st_BSGBJP.Text = right("00000"+ds_MST040PF.GetColumn(0,"MJINT"),5);
	ed_SGBJP.Text = toNumber(ds_MST040PF.GetColumn(0,"MJINT"))+1;

	MainDiv.Enable = false;
	MainDiv.ed_SGBVN.Enable = false;
	MainDiv.ca_SGBDT.Enable = false;
	ed_SGBJP.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32	
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_searchCodeName1.Value = comps;
}

/**********************************************************************
 * 내용설명 : 단가소급TX 전표번호 조회(MST040PF)
 **********************************************************************/
function getMST040PF(){
	http.Sync 	= true;
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " MAJCD=" + quote("BIDC210");		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST040PF.rowcount == 0){
		alert(setPopMessage("1444"));  // [경고] 시스템에 문제가 발생하였습니다. 디오네 종료 후 재가동바랍니다.
		setMessage("1444");	
	}
}
/**********************************************************************
 * 내용설명 : 단가소급TX 전표번호 저장(MST040PF)
 **********************************************************************/
 function setMst040pf()
 {
	http.Sync = true;
	var sKind		= "setmst040";
	var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("BIDC210");
	sArgument += " MJINT=" + quote(ed_SGBJP.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBIDC210(){

	if(length(trim(ed_SGBJP.Text)) < 1){
		alert(setPopMessage("4012"));  //[[확인]  입력바랍니다.
		setMessage("4012");	
		ed_SGBJP.SetFocus();
		return;
	}
	//조회
	var sKind		= "getBIDC210";	
	var sMethodName = "service::psimmlib/selectBIDC210.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BIDC210=ds_BIDC210";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@SGBJP=" + quote(ed_P_SGBJP.Text + "-" + right("00000"+ed_SGBJP.Text,5));

	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	

	if (str == 'T'){  //정상조회
		MainDiv.ed_SGBVN.Text =  ds_BIDC210.GetColumn(0,"SGBVN");
		MainDiv.ca_SGBDT.Value =  ds_BIDC210.GetColumn(0,"SGBDT");
		MainDiv.st_VNAME.Text =  ds_BIDC210.GetColumn(0,"VNAME");
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //   [알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	ed_SGBJP.SetFocus();
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setBIDC210(){
	if(length(trim(MainDiv.ed_SGBVN.Value)) < 1){
		alert(setPopMessage("4674"));  //  
		setMessage("4674");
		return;
	}
	if(length(trim(MainDiv.ca_SGBDT.Value)) < 8){
		alert(setPopMessage("4027"));  //  [확인]전표일자를 확인바랍니다.
		setMessage("4027");
		return;
	}

	ds_BIDC210.fireEvent = false;
	ds_BIDC210.UpdateControl = false;
	for(i=0;i<ds_BIDC210.rowcount;i++){
		var c_SGBJP = ds_BIDC210.GetColumn(i,"SGBJP");
		var c_SGBQY = ds_BIDC210.GetColumn(i,"SGBQY");
		var c_SGBST = ds_BIDC210.GetColumn(i,"SGBST");

		if (c_SGBJP == null || c_SGBJP == ""){
			ds_BIDC210.SetRowType(i,"normal");
		}else{
			if(c_SGBQY == null){
				ds_BIDC210.SetColumn(i,"SGBQY","0");
			}
			if(c_SGBST == null){
				ds_BIDC210.SetColumn(i,"SGBST","0");
			}
		}		
	}
	ds_BIDC210.fireEvent = true;

	var sKind		= "SAVE_BIDC210";						
	var sMethodName = "service::psimmlib/insertBIDC210.do";
	var sInDataSet  = "ds_BIDC210=ds_BIDC210:U";
	var sOutDataSet = "ds_BIDC210_CHECK=ds_BIDC210_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " MSIPNO=" + quote(ed_SGBJP.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid| ";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "SAVE_BIDC210"){
		if (ds_BIDC210_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_BIDC210_CHECK.GetRowCount();i++){		
				alert(ds_BIDC210_CHECK.GetColumn(i,"SGBJP") + " , "
				    + ds_BIDC210_CHECK.GetColumn(i,"SGBPN") + " ="
					+ ds_BIDC210_CHECK.GetColumn(i,"RTMSG"));	
			}
			//return;		
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		if(toNumber(ed_SGBJP.Text) > toNumber(right(st_BSGBJP.Text,5))){
			setMst040pf(); //납품대금 수금번호 저장(MST040PF)
		}
		alert(setPopMessage("1001"));
		setMessage("1001");	
		//getBIDC210();
		getMST040PF();
		st_BSGBJP.Text = right("00000"+ds_MST040PF.GetColumn(0,"MJINT"),5);
		//ed_SGBJP.Text = toNumber(ds_MST040PF.GetColumn(0,"MJINT"))+1;
		ed_SGBJP.Text = "";
		MainDiv.Enable = false;
	}
}

