function fn_Input()
{
	if(length(trim(ed_SGBJP.Text)) < 1){
		alert(setPopMessage("4012"));  //[[확인] 전표번호를 입력바랍니다.
		setMessage("4012");	
		ed_SGBJP.SetFocus();
		return;
	}
	if(checkInsertCount("ds_BIDC210")== false) return;

	getBIDC210_DCHK();
	ed_Action.Text = "A";
	if (toNumber(ds_BIDC210_DCHK.GetColumn(0,"CNT")) != 0){
		if(confirm("이미 등록된 자료입니다. 기등록된 자료에 추가하시겠습니까? ") == true){
			var cnt1 =ds_BIDC210.rowcount;
			if(cnt1<1) getBIDC210();  //조회->수정->추가클릭시에는 다시조회하지 않는다.
			var cnt2 =ds_BIDC210.rowcount;
			ds_BIDC210.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			//var seqMaxNumber = toNumber(ds_BIDC210_DCHK.GetColumn(0,"SEQ")); //마지막 일련번호 가져옴.
			//var seqNumber = seqMaxNumber;
			for (var i=cnt2;i<18;i++){
			//	seqNumber = seqNumber+1;
				ds_BIDC210.AddRow();
				ds_BIDC210.SetColumn(ds_BIDC210.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
				ds_BIDC210.SetColumn(ds_BIDC210.Row, "SGBJP", ed_P_SGBJP.Text + "-" + right("00000"+ed_SGBJP.Text,5)); // default값 설정
				ds_BIDC210.SetColumn(ds_BIDC210.Row, "SGBVN", MainDiv.ed_SGBVN.Text);// default값 설정 ----업체코드
				ds_BIDC210.SetColumn(ds_BIDC210.Row, "SGBDT", MainDiv.ca_SGBDT.Value);// default값 설정----일자
			}
			ds_BIDC210.FireEvent=true;
			MainDiv.Enable = false;
			MainDiv.ed_SGBVN.Enable = false;
			MainDiv.ca_SGBDT.Enable = false;
			MainGrid.SetFocus();
			ds_BIDC210.rowposition = cnt2;
			MainGrid.SetCellPos(0);
		}else{
			//alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			//setMessage("1520");	
			ed_SGBJP.SetFocus();
			return;
		}
	}else{
		if(toNumber(ed_SGBJP.Text) != toNumber(right(st_BSGBJP.Text,5))+1){
			alert(setPopMessage("4012"));  //[[확인] 전표번호를 입력바랍니다.
			setMessage("4012");	
			ed_SGBJP.SetFocus();
			return;
		}
		ds_BIDC210.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
		for (var i=0;i<18;i++){
			ds_BIDC210.AddRow();
			ds_BIDC210.SetColumn(ds_BIDC210.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
			ds_BIDC210.SetColumn(ds_BIDC210.Row, "SGBJP", ed_P_SGBJP.Text + "-" + right("00000"+ed_SGBJP.Text,5)); // default값 설정
		}
		ds_BIDC210.rowposition = 0;
		ds_BIDC210.FireEvent=true;	
		MainDiv.Enable = true;
		MainDiv.ed_SGBVN.Enable = true;
		MainDiv.ca_SGBDT.Enable = true;
		MainDiv.ed_SGBVN.SetFocus();
	}


}

