}


function ds_BIDC210_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;  //transaction호출후 저장이벤트가 발생되지 않아서...
	if( strColumnID == "SGBPN" ){
		if (length(varNewValue) < 1) {
			alert(setPopMessage("2501"));  //[경고] 필수입력항목입니다. (품번)
			setMessage("2501");	
			return false;
		}
		//업체코드,전표일자미등록시 진행불가
		if(length(trim(MainDiv.ed_SGBVN.Value)) < 1){
			alert(setPopMessage("4674"));  //  [알림]업체를 입력하세요.
			setMessage("4674");
			return false;
		}
		if(length(trim(MainDiv.ca_SGBDT.Value)) < 1){
			alert(setPopMessage("4027"));  //  [확인]전표일자를 확인바랍니다.
			setMessage("4027");
			return false;
		}
		//중복Check
		if(ds_BIDC210.FindRow("SGBPN", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");	
			return false;
		}
		//품번Check,조회
		http.Sync = true;
		var sKind		= "getBM008PR";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_BM008PR.GetRowCount() < 1){
			alert(setPopMessage("1501"));  //[확인] 품번 확인바랍니다.
			setMessage("1501");	
			return false;
		}
		can_ITDSC = ds_BM008PR.GetColumn(0,"ITDSC");
		can_UNMSR = ds_BM008PR.GetColumn(0,"UNMSR");
		can_ITCLS = SUBSTR(ds_BM008PR.GetColumn(0,"ITCLS"),0,1);
	}

	http.usewaitcursor = true;
}

function ds_BIDC210_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "SGBPN"){
		ds_BIDC210.SetColumn(nRow,"SGBVN", MainDiv.ed_SGBVN.Text);// default값 설정 ----업체코드
		ds_BIDC210.SetColumn(nRow,"SGBDT", MainDiv.ca_SGBDT.Value);// default값 설정----일자
		ds_BIDC210.SetColumn(nRow,"ITDSC",can_ITDSC);  // default값 설정
		ds_BIDC210.SetColumn(nRow,"SGBUM",can_UNMSR);  // default값 설정
		ds_BIDC210.SetColumn(nRow,"SGBIC",can_ITCLS);  // default값 설정
	}
	if (strColumnID == "SGBQY"){
		var surang = iif(varNewValue == "",0,toNumber(varNewValue));
		var danga = iif(ds_BIDC210.GetColumn(nRow,"SGBST") == "",0,toNumber(ds_BIDC210.GetColumn(nRow,"SGBST")));
		ds_BIDC210.SetColumn(nRow,"SGBMT",surang*danga);
	}
	if (strColumnID == "SGBST"){
		var danga = iif(varNewValue == "",0,toNumber(varNewValue));
		var surang = iif(ds_BIDC210.GetColumn(nRow,"SGBQY") == "",0,toNumber(ds_BIDC210.GetColumn(nRow,"SGBQY")));
		ds_BIDC210.SetColumn(nRow,"SGBMT",surang*danga);
	}
}

function MainDiv_ed_SGBVN_OnChanged(obj,strText)
{
	http.Sync 	= true;
	var sKind		= "getVENNAM";		
	var sMethodName = "service::pscomlib/selectVENNAM.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_VENNAM=ds_VENNAM";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " VNDNR=" + quote(strText);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_VENNAM.GetRowCount() != 1){
		alert(setPopMessage("4674"));
		setMessage("4674");	
		MainDiv.st_VNAME.Text = "";
		MainDiv.ed_SGBVN.SetFocus();
		return;
	}
	MainDiv.st_VNAME.Text = ds_VENNAM.GetColumn(0,"VNAME");
}



function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");
		}
	}
}

function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}
