	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor1");
	Static7.BkColor = G_ds_design.GetColumn(0,"BTBkColor1");	
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;	

}

/********************************************************************** 
 * 내용설명 : 화면조회
 **********************************************************************/ 
function getBJIZ101Q(){
	var sKind		= "ds_BJIZ110";						
	var sMethodName = "service::psimmlib/selectBJIZ110.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJIZ110=ds_BJIZ110";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += "@SQLID=" + quote("getBJIZ121");	

	if(ed_RPIPN_ST.Value != ""){
		sArgument += "@RPIJP_BEFORE=" +  quote('RP-' + ed_RPIPN_ST.Value);
	}	

	if(ed_RPIPN_ED.Value != ""){
		sArgument += "@RPIJP_AFTER="  +  quote('RP-' + ed_RPIPN_ED.Value);
	}	

	sArgument += "@RPIVN_BEFORE=" + quote(ed_RPIVN_ST.Value);
	sArgument += "@RPIVN_AFTER=" + quote(ed_RPIVN_ST.Value);

	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");		
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "저장품 거래 명세서";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid| 저장품 거래 명세서";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBJIZ101Q"){
		// 조회건수가 없을경우
		if(ds_BJIZ110.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

