function fn_Search()
{
	if(cb_searchCodeName.value=""){
		alert("사업부를 선택하십시요.");		
	} else if(ed_SMOCL.value=""){
		alert("분류코드를 입력하십시요.");
	} else {
		getBJMZ101Q();
	}
}

