function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode(); //공장구분 선택
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;	
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");

	displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getBJMZ110(){
	//var group = ComboBoxClass.Value;

	if(!validationChk()) return;

	var sKind		= "getBJMZ110";						
	var sMethodName = "service::psimmlib/selectBJMZ110.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJMZ110=ds_BJMZ110";    // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);  // 사업부
	sArgument += "@SMOCL=" + quote(ed_smocl.Value);       // 분류코드
	sArgument += "@SMOPN=" + quote(ed_smopn.Value);       // 품번

	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
		ed_smonm.Text = ds_BJMZ110.GetColumn(0,"SMONM");
		ed_smoum.Text = ds_BJMZ110.GetColumn(0,"SMOUM");
		ed_smobd.Text = ds_BJMZ110.GetColumn(0,"SMOBD");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setBJMZ110(){
	if(!validationChk()) return;
	if(!validationChk2()) return;

	if(ds_BJMZ110.rowcount() < 1){
		alert("데이터를 조회 하십시요.");
		return;
	}

	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);

	var sKind		= "setBJMZ110";						
	var sMethodName = "service::psimmlib/saveBJMZ110.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_BJMZ110=ds_BJMZ110:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJMZ110_CHECK=ds_BJMZ110_CHECK";     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	/*
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);  // 사업분류
	sArgument += " SMOCL=" + quote(ed_smocl.Value);       // 분류코드
	sArgument += " SMOPN=" + quote(ed_smopn.Value);       // 품번

	sArgument += " IDATE=" + quote(cal_IDATE.Value);  	  // 적용일자
	sArgument += " ICOST=" + quote(displayDiv.med_icost.Value);       // 단가
	sArgument += " IMARK=" + quote(displayDiv.ed_imark.Value);       // 참조사항
	*/
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "setBJMZ110"){
		if (ds_BJMZ110_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_BJMZ110_CHECK.GetRowCount();i++){		
				alert(ds_BJMZ110_CHECK.GetColumn(i,"GRPCD") + " , "
					+ ds_BJMZ110_CHECK.GetColumn(i,"MSTCD") + " = "
					+ ds_BJMZ110_CHECK.GetColumn(i,"RTMSG"));	
			}
			getBJMZ110();
			return;	
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setBJMZ110"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : validation 체크 1
 **********************************************************************/ 
function validationChk(){

	if(length(trim(ed_smocl.Text))==0){
		alert("분류 코드를 입력해 주세요.");
		ed_smocl.SetFocus();
		return false;
	}

	if(length(trim(ed_smopn.Text))==0){
		alert("품번을입력해 주세요.");
		ed_smopn.SetFocus();
		return false;
	}

	return true;
}
/********************************************************************** 
 * 내용설명 : validation 체크 2
 **********************************************************************/ 
function validationChk2(){
	for(var i=0; i<ds_BJMZ110.rowcount(); i++){
		if(length(trim(ds_BJMZ110.GetColumn(i, "IDATE")))==0){
			alert(setPopMessage("2507"));
			setMessage("2507");
			mainGrid.SetFocus();
			ds_BJMZ110.rowposition = i;
			MainGrid.SetCellPos(0);
			return false;
		}
		if(length(trim(ds_BJMZ110.GetColumn(i, "ICOST")))==0){
			ds_BJMZ110.FireEvent = false; //데이터셋의 이벤트가 발생하지 않도록 셋팅
			ds_BJMZ110.SetColumn(i,"ICOST","0");
			ds_BJMZ110.FireEvent = true;
		}
	}
	return true;
}
/********************************************************************** 
 * 내용설명 : 중복 체크
 **********************************************************************/ 
function duplicateChk(){
	http.Sync 	= true;
	var sKind		= "getBJMZ110_dchk";						
	var sMethodName = "service::psimmlib/selectBJMZ110_dchk.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BJMZ110_dchk=ds_BJMZ110_dchk";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GRPCD=" + quote(displayDiv.ed_grpcd.Value);       //코드분류
	sArgument += " MSTCD=" + quote(displayDiv.ed_mstcd1.Value);      //코드	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if(ds_BJMZ110_dchk.rowcount() > 0){
		alert(setPopMessage("2039"));	// 2039 == 중복된data가 존재합니다..
		setMessage("2039");
		return false;
	}
	return true;	
}

/********************************************************************** 
 * 내용설명 : validation 체크 (notnull,중복)
 **********************************************************************/ 
function ds_BJMZ110_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "IDATE" ){
		if( length(varNewValue) == 0){
			alert(setPopMessage("2507"));
			setMessage("2507");
			return false;
		}
	}
	if( strColumnID == "ICOST" ){
		if( length(varNewValue) == 0 || toNumber(varNewValue) < 0){
			alert(setPopMessage("2507"));
			setMessage("2507");
			return false;
		}
	}
}

