/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_BJMZ110_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_BJMZ110.GetRowType(ds_BJMZ110.currow)=="insert"){
		displayDiv.cal_idate.Enable=true;
		displayDiv.med_icost.Enable=true;
		displayDiv.ed_imark.Enable=true;
	}else{
		displayDiv.cal_idate.Enable=false;
		displayDiv.med_icost.Enable=true;
		displayDiv.ed_imark.Enable=true;
	}
}



