function fn_Input()
{
	if(checkInsertCount("ds_BJMZ110")== false) return;

	if(ds_BJMZ110.rowcount() < 1){
		getBJMZ110();
	}

	ds_BJMZ110.AddRow();
	ds_BJMZ110.SetColumn(ds_BJMZ110.Row, "COMPS", ComboBoxPlant.Value); // default값 설정
	ds_BJMZ110.SetColumn(ds_BJMZ110.Row, "SMOCL", ed_smocl.Value); // default값 설정
	ds_BJMZ110.SetColumn(ds_BJMZ110.Row, "SMOPN", ed_smopn.Value); // default값 설정
	displayDiv.cal_idate.SetFocus();
}

