function displayDiv_cal_IDATE_CanChange(obj,strOldText,strText)
{
	for(var i=0; i<ds_BJMZ110.rowcount(); i++){
		if(trim(ds_BJMZ110.GetColumn(i,"IDATE")) == strText){
			alert("중복된 날짜가 존재 합니다. 확인 해주세요.");
			displayDiv.SetFocus();
			return false;
		}
	}	
