function getComCode(){

	http.Sync=true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	Div0.cb_searchCodeName1.Value=comps;

}
/********************************************************************** 
 * 내용설명 : 생성
 * 주요변수 : 
 **********************************************************************/ 
function setBJOZ119C(){
	var compsCode = trim(Div0.cb_searchCodeName1.Value);	

	if(confirm("저장품 출고 마감작업입니다. 작업하시겠습니까? ") == false) return;

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	var sKind		= "setBJOZ110C";
	var sMethodName = "service::psimmlib/saveBJOZ110C.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    										// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- svcId	Transaction 고유 ID
 * 			- rtnCd 	에러코드
 * 			- rtnMsg 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		ReleaseCapture();
		SetWaitCursor(false);
		alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
		setMessage("1002");
	}else{
		ReleaseCapture();
		SetWaitCursor(false);
		st_Msg.Visible = false;
		alert(setPopMessage("1001")); //[알림] 정상적으로 처리가 완료되었습니다.
		setMessage("1001");
	}
}

