/********************************************************************** 
  * 내용설명 : 화면로드
  **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
  **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static4.BkColor = G_ds_design.GetColumn(0,"BTBkColor1");
	//Static1.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");	
	//Static7.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");		

	cal_ISIDT_BEFORE.Value = SubStr(nowDate, 0, 6) +"01";
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	cb_comp.Value = comps;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBJOZ122Q(){

	var sArgument = "";
	var sKind		= "getBJOZ122Q";						
	var sMethodName = "service::psimmlib/selectBJOZ.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BJOZ122Q=ds_BJOZ122Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sqlid = "";
	if(rdoggub.Value == "1"){
		sqlid = "selectBJOZ1221Q";
	} else if(rdoggub.Value == "2"){
		sqlid = "selectBJOZ1222Q";
	}
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@SQLID=" + sqlid;
	sArgument += "@COMPS=" + quote(cb_comp.Value);
	sArgument += "@ISICL=" + quote(ed_ISICL.Value);
	sArgument += "@ISIPN=" + quote(ed_ISIPN.Value);
	sArgument += "@ISIDT_BEFORE=" + quote(cal_ISIDT_BEFORE.Value);
	sArgument += "@ISIDT_AFTER=" + quote(cal_ISIDT_AFTER.Value);
	http.TimeOut = 600;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때

		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "출고내역조회(품번일자별)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|출고내역조회(품번일자별)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBHZC101Q"){
		// 조회건수가 없을경우
		if(ds_BJOZ122Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");	
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}

