function setPrint()
{
	getSYSDATE(); //SYSTEM 날짜
	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");
	ubi0.setJrfFileDir(G_baseURL+"ui/PSIMMLIB/REPORT/");
	//C:\eclipse\workspace\DENSO\WebContent
	//ubi0.setJrfFileDir("C:/eclipse/workspace/DENSO/WebContent"+"/ui/PSIMMLIB/REPORT/");
	ubi0.setJrfFileName("BODC109R.jrf");
	ubi0.SetDataset("ds_BODC109R", "ds_BODC109R");	
	//ubi0.SetArgument("COMPS", cb_searchCodeName1.Value);
	//ubi0.SetArgument("YM", substr(cal_JSOYM.Value,0,6));
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);
	ubi0.Retrieve();
}

function getBODC109R(){
	var p_hou1,p_hou2;
	p_hou1 = iif (length(trim(ed_HOUSE1.Text)) == 0, '1', trim(ed_HOUSE1.Text));
	p_hou2 = iif (length(trim(ed_HOUSE2.Text)) == 0, '3', trim(ed_HOUSE2.Text));
	var p_tno1,p_tno2;
	p_tno1 = iif (length(trim(ed_TRKNO1.Text)) == 0, '0', trim(ed_TRKNO1.Text));
	p_tno2 = iif (length(trim(ed_TRKNO2.Text)) == 0, '9999999', trim(ed_TRKNO2.Text));			
	var sKind		= "getBODC109R";						
	var sMethodName = "service::psimmlib/selectBODC109R.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BODC109R=ds_BODC109R";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " HOUSE1=" + quote(p_hou1); 
	sArgument += " HOUSE2=" + quote(p_hou2); 
	sArgument += " TRKNO1=" + quote(p_tno1);
	sArgument += " TRKNO2=" + quote(p_tno2);
	sArgument += " YMD=" + quote(today());

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");


}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBODC109R"){
		// 조회건수가 없을경우
		if(ds_BODC109R.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			setPrint();
		}
	}
}

