/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBODE125Q(){
	var p_pno1,p_pno2;
	p_pno1 = iif (length(trim(ed_PSPNO1.Text)) == 0, ' ', trim(ed_PSPNO1.Text));
	p_pno2 = iif (length(trim(ed_PSPNO2.Text)) == 0 , '999999999999999', trim(ed_PSPNO2.Text));
	var p_vnd1,p_vnd2;
	p_vnd1 = iif (length(trim(ed_VENDR1.Text)) == 0, ' ', trim(ed_VENDR1.Text));
	p_vnd2 = iif (length(trim(ed_VENDR2.Text)) == 0 , '999999', trim(ed_VENDR2.Text));

	//기준년월
	var p_comps;
	if(company == "PE"){
		p_comps = "E1";
	}else{
		p_comps = "C1";
	}
	var cigmaDT = "(SELECT ADSDT FROM "+Lib1+".SC000PR)" ;
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(p_comps); //사업부
		sArgument += " CHKDT=" + quote("PDATE"); //시작일자 PARAM
		sArgument += " CHKCNT=" + quote("1"); //리턴수
		sArgument += " STDDT=" + quote(cigmaDT); //시작일자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	var YMD = ds_MST090PF.GetColumn(0,"PRCDT");
	MainGrid.SetCellProp("Head", 12, "text", substr(YMD,0,4)+"/"+substr(YMD,4,2));
	MainGrid.SetCellProp("Head", 13, "text", substr(AddMonth(YMD,1),0,4)+"/"+substr(AddMonth(YMD,1),4,2));
	MainGrid.SetCellProp("Head", 14, "text", substr(AddMonth(YMD,2),0,4)+"/"+substr(AddMonth(YMD,2),4,2));
	MainGrid.SetCellProp("Head", 15, "text", substr(AddMonth(YMD,3),0,4)+"/"+substr(AddMonth(YMD,3),4,2));
	MainGrid.SetCellProp("Head", 16, "text", substr(AddMonth(YMD,4),0,4)+"/"+substr(AddMonth(YMD,4),4,2));

	var p_plnym = substr(YMD,0,6);

	//사내창고
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAC'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
		sArgument   += " LIB1=" + quote(Lib1);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseHACT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouseHAC = "('";
	for (var i=0 ; toNumber(length(arrHouseHACT)) > i ; i++){
		if (i == length(arrHouseHACT)-1){
			arrHouseHAC = arrHouseHAC+substr(arrHouseHACT,i,1) + "')";
		}else{
			arrHouseHAC = arrHouseHAC+substr(arrHouseHACT,i,1) + "','";
		}
	}
	//사급창고
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAP'";      //HAC:사내창고        HAP:사급           HAR:중간창고           HAB:창고가능
		sArgument   += " LIB1=" + quote(Lib1);	
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseHAPT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouseHAP = "('";
	for (var i=0 ; toNumber(length(arrHouseHAPT)) > i ; i++){
		if (i == length(arrHouseHAPT)-1){
			arrHouseHAP = arrHouseHAP+substr(arrHouseHAPT,i,1) + "')";
		}else{
			arrHouseHAP = arrHouseHAP+substr(arrHouseHAPT,i,1) + "','";
		}
	}

	var sKind		= "getBODE125Q";						
	var sMethodName = "service::psimmlib/selectBODE125Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BODE125Q=ds_BODE125Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(p_comps);
	sArgument += "@PLNYM=" + quote(p_plnym);
	sArgument += "@VENDR1=" + quote(p_vnd1);
	sArgument += "@VENDR2=" + quote(p_vnd2);
	sArgument += "@PSPNO1=" + quote(p_pno1);
	sArgument += "@PSPNO2=" + quote(p_pno2);
	sArgument += "@HOUSE_HAP=" + quote(arrHouseHAP);
	sArgument += "@HOUSE_HAC=" + quote(arrHouseHAC);
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "협력업체 월소요량 현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|협력업체 월소요량 현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getBODE125Q"){
		// 조회건수가 없을경우
		if(ds_BODE125Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	}
}

