/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBSME154Q(){
	//조회
	//속도(ERROR)문제로 FROM-TO삭제
	//var spcodFrom; 
	//var spcodTo; 
	//spcodFrom = iif(trim(ed_SPCOD_from.Text) == null,"0000",ed_SPCOD_from.Text);
	//spcodTo = iif(trim(ed_SPCOD_to.Text) == null,"9999",ed_SPCOD_to.Text);
	if(length(trim(ed_SPCOD_from.Text))<1){
		alert(setPopMessage("4671"));  //[확인] 업체 확인바랍니다.
		setMessage("4671");	
		ed_SPCOD_from.SetFocus();
		return;
	}
	if(length(trim(st_CUSNM.Text))<1){
		alert(setPopMessage("4671"));  //[확인] 업체 확인바랍니다.
		setMessage("4671");	
		ed_SPCOD_from.SetFocus();
		return;
	}
	var prtnoFrom;
	var prtnoTo;
	prtnoFrom = iif(trim(ed_PRTNO_from.Text) == null," ",ed_PRTNO_from.Text);
	prtnoTo = iif(trim(ed_PRTNO_to.Text) == null,"999999999999999",ed_PRTNO_to.Text);
	var sKind		= "getBSME154Q";
	var sMethodName = "service::psimmlib/selectBSME154Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BSME154Q=ds_BSME154Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@CONNO1=" + quote(trim(ed_SPCOD_from.Text));
	//sArgument += "@CONNO2=" + quote(spcodTo);
	sArgument += "@PRTNO1=" + quote(prtnoFrom);
	sArgument += "@PRTNO2=" + quote(prtnoTo);
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "사급모품번구성마스타현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|사급모품번구성마스타현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


