function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("BUUZ159CP");
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
	Div0.st_beforYM.Text = substr(ds_MST040PF.GetColumn(0,"MJINT"),0,4)+"-"+substr(ds_MST040PF.GetColumn(0,"MJINT"),4,2);
	next_month = AddMonth(ds_MST040PF.GetColumn(0,"MJINT")+"01",1);
	//alert(next_month);
}
/********************************************************************** 
 * 내용설명 : 마감
 * 주요변수 : 
 **********************************************************************/ 
function setBUUZ159C(){
	if(length(trim(Div0.cb_searchCodeName1.Value)) < 1){
		alert(setPopMessage("1006"));  //[알림] 지원하지 않는 시스템 입니다. 정보관리팀으로 문의하십시요.
		setMessage("1006");	
		return;
	}
	if(!(Div0.ed_HOUSE.Value == "1" || Div0.ed_HOUSE.Value == "2"  || Div0.ed_HOUSE.Value == "3")) {
		alert(setPopMessage("5001"));  // [확인] 마감구분을 확인바랍니다.
		setMessage("5001");	
		Div0.ed_HOUSE.SetFocus();
		return;
	}
	//SUB CONTRACTOR MA CHECK
	http.Sync 	= true;
	var sKind		= "getEM040PR";		
	var sMethodName = "service::pscomlib/selectEM040PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_EM040PR=ds_EM040PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CONNO=" + quote(trim(Div0.ed_CONNO.Text));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_EM040PR.GetRowCount() < 1){
		alert(setPopMessage("1512"));   //  [확인] SUB CONTRACTOR MASTER에 사급업체로 등록되지 않았습니다.
		setMessage("1512");	
		return ;
	}

	if(confirm("유상사급 재고조정TX 마감 작업입니다. 작업하시겠습니까? ") == false) return;

	/* 삭제 : 2019.11.28 NEWDIONE로 SSO관리전환
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	if (length(trim(ds_ssoaccount.GetColumn(0,"SYSTEM_ID"))) < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	*/
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");

	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}



	//리모트콜 실행(BUUZ159RC)
	param = Div0.cb_searchCodeName1.Value + Div0.ed_HOUSE.Value + "0000"+Div0.ed_CONNO.Value ;
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			st_Msg.Visible = false;
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//마감history에 저장
				var sKind		= "setmst049";						
				var sMethodName = "service::pscomlib/saveMST049.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
				sArgument += " PGSRC=" + quote("BUUZ159C");
				sArgument += " PGMNM=" + quote("유상사급재고조정TX마감작업");
				sArgument += " PDATE=" + quote("0");
				sArgument += " JOBID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
				sArgument += " BIGO1=" + quote(Div0.ed_HOUSE.Value + "_"+Div0.ed_CONNO.Value);
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

				alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				setMessage("1001");	
				return;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("5008"));  //[오류] 마감처리는 진행되었으나 마감년월을 관리마스타(MST040PF)에 저장 실패. 
		setMessage("5008");
	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}

