/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBUYZ103Q(){
	//조회
	var dateFrom;
	var dateTo;
	if(length(ca_plnym_to.Value) < 8){
		dateTo = 99991231;
	}else{
		dateTo = ca_plnym_to.Value;
	}
	dateFrom = ca_plnym_from.Value;
	var spcodFrom;
	var spcodTo;
	spcodFrom = iif(trim(ed_SPCOD_from.Text) == null," ",ed_SPCOD_from.Text);
	spcodTo = iif(trim(ed_SPCOD_to.Text) == null,"999999",ed_SPCOD_to.Text);
	var prtnoFrom;
	var prtnoTo;
	prtnoFrom = iif(trim(ed_PRTNO_from.Text) == null," ",ed_PRTNO_from.Text);
	prtnoTo = iif(trim(ed_PRTNO_to.Text) == null,"9999-9999",ed_PRTNO_to.Text);
	var sKind		= "getBUYZ103Q";
	var sMethodName = "service::psimmlib/selectBUYZ103Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BUYZ103Q=ds_BUYZ103Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@SPCOD1=" + quote(spcodFrom);
	sArgument += "@SPCOD2=" + quote(spcodTo);
	sArgument += "@PRTNO1=" + quote(prtnoFrom);
	sArgument += "@PRTNO2=" + quote(prtnoTo);
	sArgument += "@MDATE1=" + quote(dateFrom);
	sArgument += "@MDATE2=" + quote(dateTo);
	sArgument += "@GUBUN=" + quote(cmb_gubun.Value);
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "외주품단가마스타현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|외주품단가마스타현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


