var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var userId = G_ds_user.GetColumn(0,"USER_ID");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.3"); //조회, 추가, 저장
	getComCode(); //공장구분 선택
	//ComboBoxPlant.Value="C1";
	//ed_ISPRC.Text="A150";
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
}

function getBZOC500(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getBZOC500";						
	var sMethodName = "service::psimmlib/selectBZOC500.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var	sOutDataSet = "ds_BZOC500=ds_BZOC500";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@ISPRC=" + quote(ed_ISPRC.Text); //공정코드
		sArgument += "@FDATE_F=" + quote(substr(cmb_FDATE.Value,0,6)+"01"); //월시작일
		sArgument += "@FDATE_T=" + quote(cmb_FDATE.Value); //마감일자
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
		cmb_PRCD.Enable=true;
		cmb_PRCD.Value=ds_BZOC500.GetColumn(0,"PRCD");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setBZOC500(){

	if(!validationChk()){
		return;
	}
	var sKind		= "setBZOC500";						
	var sMethodName = "service::psimmlib/saveBZOC500.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";
	var sOutDataSet = "";
	var sArgument   = "";
		//sArgument += " LIB2=" + quote(Lib2);
		sArgument += " BSCOMP=" + quote(ComboBoxPlant.Value);	//사업부
		sArgument += " BSDATE=" + quote(cmb_FDATE.Value);		//확인일자
		sArgument += " BSPROC=" + quote(ed_ISPRC.Text);			//공정코드
		sArgument += " BSPRCD=" + quote(cmb_PRCD.Value);		//확인코드
		sArgument += " SUSER=" + quote(userId); //사용자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	ed_ISPRC.BKColor="white";
	cmb_FDATE.BKColor="white";

	if(length(trim(ed_ISPRC.Text)) == 0){
		ed_ISPRC.BKColor="paleturquoise";
		//alert("[알림]공정코드를 입력하세요.");
		alert(setPopMessage("2026"));///[알림] 공정코드를 입력하세요.
		setMessage("2026");
		result = false;
	}
	else if(cmb_FDATE.Value == ""){
		cmb_FDATE.BKColor="paleturquoise";
		//alert("[알림]마감일자를 입력하세요.");
		alert(setPopMessage("6136"));//[알림]마감일자를 입력하세요.
		setMessage("6136");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
/*
	if (ds_BZOC500_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_BZOC500_CHECK.GetRowCount();i++){		
			alert("공정코드 : " +ds_BZOC500_CHECK.GetColumn(i,"PROCS") + " = " + ds_BZOC500_CHECK.GetColumn(i,"RTMSG"));
		}
		return;		
	}
*/
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_BZOC500.ClearData();
	cmb_PRCD.Index=-1;
	cmb_PRCD.Enable=false;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "부품출고확인(제조)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|부품출고확인(제조)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);	
}

