function ed_ISPRC_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST060PF";						
	var sMethodName = "service::pscomlib/selectMST060PF.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST060PF=ds_MST060PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " PROCS=" + quote(ed_ISPRC.Text);	//공정코드
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	/*A150없는 공정코드임.
	if (ds_MST060PF.GetRowCount() < 1){
		alert("[알림]작업공정정보마스타에 미등록된 공정코드입니다.");
		//alert(setPopMessage("6135"));//[알림]작업공정정보마스타에 미등록된 공정코드입니다.
		setMessage("6135");	
		ed_ISPRC.SetFocus();
		return;
	}
	*/
	//st_ISPRSM.Text=ds_MST060PF.GetColumn(0,"PROSM");
	st_ISPRML.Text=ds_MST060PF.GetColumn(0,"PRONM");
	http.usewaitcursor = true;	
}

function imgProcs_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_ISPRC.Text);
	if (length(trim(ed_ISPRC.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "P1";
	var rtn = dialog("Common::commonPopup.xml","COMPS=" + ComboBoxPlant.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_ISPRC.Text = rtn[0];
		//st_ISPRSM.Text = rtn[1];
		st_ISPRML.Text = rtn[2];
	}
}
