/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");             //white
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");           //white
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");       //lavender
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");     //lightgrey
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");           //false
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;


}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBZOE010(){
	var sKind		= "getBZOE010";				
	var sMethodName = "service::psimmlib/selectBZOE010.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BZOE010=ds_BZOE010";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";	
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);

	http.Sync = true;
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	MainGrid.SetFocus();
	MainGrid.SetCellPos(2);
}


/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 
 function setBZOE010(){
	if(ds_BZOE010.rowcount() < 1) return;
	var j=0;
	for (var i=0;i<ds_BZOE010.rowcount();i++){
		if(ds_BZOE010.GetRowType(i) == "update") j++;
	}
	if (j==0){
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}
	var sKind		= "setBZOE010";						
	var sMethodName = "service::psimmlib/saveBZOE010.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_BZOE010=ds_BZOE010:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BZOE010_Check=ds_BZOE010_Check";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){

	if (svcId == "setBZOE010"){
		if (ds_BZOE010_Check.GetRowCount() > 0){			
			for(var i=0;i<ds_BZOE010_Check.GetRowCount();i++){		
				alert(ds_BZOE010_Check.GetColumn(i,"PROCS")+" : "+ds_BZOE010_Check.GetColumn(i,"RTMSG"));	
			}
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

